export const stringToBoolean = (value) => {

  if (value) {
    switch (value.toLowerCase().trim()) {
      case "true": case "yes": case "1": return true;
      case "false": case "no": case "0": case null: return false;
      default: return Boolean(value);
    }
  }
  else {
    return Boolean(value);
  }
}


