            const response = await Digit.UploadServices.Filestorage("property-upload", file, Digit.ULBService.getStateId());
            if (response?.data?.files?.length > 0) {
              setUploadedFile(response?.data?.files[0]?.fileStoreId);
            } else {
              setError(t("PT_FILE_UPLOAD_ERROR"));
            }
          } catch (err) {
          }
        }
      }
    })();
  }, [file]);

  return (
    <React.Fragment>
    {window.location.href.includes("/citizen") ? <Timeline currentStep={3}/> : null}
    <FormStep config={config} onSelect={handleSubmit} onSkip={onSkip} t={t} isDisabled={!uploadedFile || error}>
      <CardLabelDesc style={{ fontWeight: "unset" }}>{t(`TL_UPLOAD_PHOTO_RESTRICTIONS_TYPES`)}</CardLabelDesc>
      <CardLabelDesc style={{ fontWeight: "unset" }}>{t(`TL_UPLOAD_RESTRICTIONS_SIZE`)}</CardLabelDesc>
      <CardLabel>{`${t("TL_CATEGORY_DOCUMENT_TYPE")}`}</CardLabel>
      {/* <Dropdown
        t={t}
        isMandatory={false}
        option={dropdownData}
        selected={dropdownValue}
        optionKey="i18nKey"
        select={setTypeOfDropdownValue}
        //placeholder={t(`PT_MUTATION_SELECT_DOC_LABEL`)}
      /> */}
      <UploadFile
        id={"tl-doc"}
        extraStyleName={"propertyCreate"}
        accept=".jpg,.png,.pdf"
        onUpload={selectfile}
        onDelete={() => {
          setUploadedFile(null);
        }}
        message={uploadedFile ? `1 ${t(`TL_ACTION_FILEUPLOADED`)}` : t(`TL_ACTION_NO_FILEUPLOADED`)}
        error={error}
      />
      {error ? <div style={{ height: "20px", width: "100%", fontSize: "20px", color: "red", marginTop: "5px" }}>{error}</div> : ""}
      <div style={{ disabled: "true", height: "20px", width: "100%" }}></div>
    </FormStep>
    </React.Fragment>
  );
};

export default Proof;
import React, { useState, useEffect, useMemo } from "react";
import { FormStep, RadioOrSelect, RadioButtons, LabelFieldPair, Dropdown, CardLabel, CardLabelError } from "@egovernments/digit-ui-react-components";
import { cardBodyStyle } from "../utils";
import { useLocation } from "react-router-dom";
import Timeline from "../components/TLTimeline";

const SelectOwnerShipDetails = ({ t, config, onSelect, userType, formData, onBlur, formState, setError, clearErrors }) => {
  const tenantId = Digit.ULBService.getCurrentTenantId();
  const stateId = Digit.ULBService.getStateId();
  //const isUpdateProperty = formData?.isUpdateProperty || false;
  //let isEditProperty = formData?.isEditProperty || false;
  let isEdit = window.location.href.includes("edit-application")||window.location.href.includes("renew-trade");
  const [ownershipCategory, setOwnershipCategory] = useState(formData?.ownershipCategory);
  const { data: dropdownData } = Digit.Hooks.tl.useTradeLicenseMDMS(stateId, "common-masters", "TLOwnerTypeWithSubtypes",{userType});

  const { pathname: url } = useLocation();
  const editScreen = url.includes("/modify-application/");

  function selectedValue(value) {
    setOwnershipCategory(value);
  }

  const onSkip = () => onSelect();
  function goNext() {
    sessionStorage.setItem("ownershipCategory", ownershipCategory?.value);
    onSelect(config.key, ownershipCategory);
  }

  useEffect(() => {
    if (userType === "employee") {
      if (!ownershipCategory) setError(config.key, { type: "required", message: t(`REQUIRED_FIELD`) }); //message: `${config.key.toUpperCase()}_REQUIRED` }
      else clearErrors(config.key);
      goNext();
    }
  }, [ownershipCategory]);


  // useEffect(() => {
  //   if (userType === "employee") {
  //     setOwnershipCategory(dropdownData[0]);
  //   }
  // }, []);

  if (userType === "employee") {
  let isRenewal = window.location.href.includes("tl/renew-application-details");
  if (window.location.href.includes("tl/edit-application-details")) isRenewal = true;
  
    return (
      <React.Fragment>
        <LabelFieldPair>
          <CardLabel className="card-label-smaller" style={editScreen ? { color: "#B1B4B6" } : {}}>
            {`${t("TL_NEW_OWNER_DETAILS_OWNERSHIP_TYPE_LABEL")} * `}
          </CardLabel>
          <Dropdown
            className="form-field"
            selected={ownershipCategory}
            errorStyle={formState.touched?.[config.key] && formState.errors[config.key]?.message ? true : false}
            disable={isRenewal}
            option={dropdownData}
            select={selectedValue}
            optionKey="i18nKey"
            onBlur={onBlur}
            t={t}
          />
        </LabelFieldPair>
        {formState.touched?.[config.key] ? (
          <CardLabelError style={{ width: "70%", marginLeft: "30%", fontSize: "12px", marginTop: "-21px" }}>
            {formState.errors[config.key]?.message}
          </CardLabelError>
        ) : null}
      </React.Fragment>
    );
  }

  return (
    <React.Fragment>
    {window.location.href.includes("/citizen") ? <Timeline currentStep={2}/> : null}
    <FormStep t={t} config={config} onSelect={goNext} onSkip={onSkip} isDisabled={!ownershipCategory}>
      <RadioButtons
        isMandatory={config.isMandatory}
        options={dropdownData?dropdownData:[]}
        selectedOption={ownershipCategory}
        optionsKey="i18nKey"
        onSelect={selectedValue}
        value={ownershipCategory}
        labelKey="PT_OWNERSHIP"
        isDependent={true}
        disabled={isEdit}
      />
    </FormStep>
    </React.Fragment>
  );
};

export default SelectOwnerShipDetails;
import React, { useEffect, useState, useMemo, useReducer } from "react";
import {
  FormStep,
  TextInput,
  CardLabel,
  RadioButtons,
  LabelFieldPair,
  Dropdown,
  CheckBox,
  LinkButton,
  CardHeader,
  Loader,
} from "@egovernments/digit-ui-react-components";

import Timeline from "../components/TLTimeline";

const SelectOwnerDetails = ({ t, config, onSelect, userType, formData }) => {
  let { ownershipCategory: { code: keyToSearchOwnershipSubtype } = {} } = formData;
  const typeOfOwner = useMemo(() => {
    if (formData?.ownershipCategory?.code.includes("SINGLEOWNER")) return "SINGLEOWNER";
    if (formData?.ownershipCategory?.code.includes("INSTITUTIONAL")) return "INSTITUTIONAL";
    else return "MULTIOWNER";
  }, [formData?.ownershipCategory]);
  const storedOwnerData = formData?.owners?.owners;
  const initFn = (initData) => {
    switch (typeOfOwner) {
      case "SINGLEOWNER":
        return [
          {
            name: initData?.[0]?.name || "",
            gender: initData?.[0]?.gender,
            mobilenumber: initData?.[0]?.mobilenumber,
            isprimaryowner: true,
            fatherOrHusbandName: initData?.[0]?.fatherOrHusbandName || "",
            // subOwnerShipCategory: formData?.subOwnerShipCategory || "",
            // designation: formData?.institution?.designation || "",
            // altContactNumber: initData?.[0]?.altContactNumber || "",
            emailId: initData?.[0]?.emailId || "",
            relationship: initData?.[0]?.relationship || "",
            id: initData?.[0]?.id || "",
            uuid: initData?.[0]?.uuid || "",
          },
        ];
      case "MULTIOWNER":
        return initData?.length > 1
          ? initData?.map((owner) => ({
              name: owner?.name || "",
              gender: owner?.gender,
              mobilenumber: owner?.mobilenumber,
              isprimaryowner: owner?.isprimaryowner,
              fatherOrHusbandName: owner?.fatherOrHusbandName || "",
              emailId: initData?.emailId || "",
              relationship: owner?.relationship || "",
              id: owner.id || "",
              uuid: owner.uuid || "",
            }))
          : [
              {
                name: null,
                gender: null,
                mobilenumber: null,
                isprimaryowner: true,
                fatherOrHusbandName: null,
                emailId: null,
                relationship: null,
              },
            ];
      case "INSTITUTIONAL":
        return [
          {
            name: initData?.[0]?.name || "",
            // gender: initData?.gender,
            mobilenumber: initData?.[0]?.mobilenumber,
            // isprimaryowner: true,
            institutionName: initData?.[0]?.institutionName || "",
            subOwnerShipCategory: initData?.[0]?.subOwnerShipCategory || "",
            designation: initData?.[0]?.designation || "",
            altContactNumber: initData?.[0]?.altContactNumber || "",
            emailId: initData?.[0]?.emailId || "",
            id: initData?.[0]?.id || "",
            uuid: initData?.[0]?.uuid || "",
            // relationship: initData?.[0]?.relationship || "",
          },
        ];
    }
  };
  const reducer = (state, action) => {
    switch (action.type) {
      case "ADD_NEW_OWNER":
        return [
          ...state,
          {
            name: "",
            gender: null,
            mobilenumber: null,
            relationship: "",
            fatherOrHusbandName: "",
            emailId: "",
            isprimaryowner: false,
          },
        ];
      case "REMOVE_THIS_OWNER":
        return state.filter((e, i) => i !== action?.payload?.index);
      case "SET_PRIMARY_OWNER":
        if (action?.payload?.index) {
          return state.map((ownerData, i) => {
            if (i === action?.payload?.index) {
              return { ...ownerData, isprimaryowner: true };
            } else {
              return { ...ownerData, isprimaryowner: false };
            }
          });
        } else {
          return state;
        }
      case "EDIT_CURRENT_OWNER_PROPERTY":
        return state.map((data, __index) => {
          if (__index === action.payload.index) {
            return { ...data, [action.payload.key]: action.payload.value };
          } else {
            return data;
          }
        });
    }
  };

  const [formState, dispatch] = useReducer(reducer, storedOwnerData, initFn);

  const relationshipMenu = [
    {
      code: "FATHER",
      i18nKey: "COMMON_RELATION_FATHER",
    },
    {
      code: "HUSBAND",
      i18nKey: "COMMON_RELATION_HUSBAND",
    },
  ];

  const stateId = Digit.ULBService.getStateId();
  const isEdit = window.location.href.includes("/edit-application/") || window.location.href.includes("/renew-trade/");

  const { data: Menu, isLoading: isGenderLoading } = Digit.Hooks.tl.useTLGenderMDMS(stateId, "common-masters", "GenderType");
  if (isEdit) keyToSearchOwnershipSubtype = keyToSearchOwnershipSubtype.split(".")[0];
  const { data: institutionOwnershipTypeOptions } = Digit.Hooks.tl.useTradeLicenseMDMS(stateId, "common-masters", "TradeOwnershipSubType", {
    keyToSearchOwnershipSubtype,
  });

  let TLmenu = [];
  Menu &&
    Menu.map((genders) => {
      TLmenu.push({ i18nKey: `TL_GENDER_${genders.code}`, code: `${genders.code}` });
    });

  function handleTextInputField(index, e, key) {
    dispatch({ type: "EDIT_CURRENT_OWNER_PROPERTY", payload: { index, key, value: e.target.value } });
  }

  function handleRadioButtonInput(index, value, key) {
    dispatch({ type: "EDIT_CURRENT_OWNER_PROPERTY", payload: { index, key, value } });
  }
  function handleDropdownInputField(index, value, key) {
    dispatch({ type: "EDIT_CURRENT_OWNER_PROPERTY", payload: { index, key, value } });
  }

  const [error, setError] = useState(null);

  function checkMandatoryFieldsForEachOwner(ownersData) {
    if (typeOfOwner === "INSTITUTIONAL") {
      if (ownersData[0]?.name && ownersData[0]?.subOwnerShipCategory && ownersData[0]?.mobilenumber) {
        setError("TL_ERROR_FILL_ALL_MANDATORY_DETAILS");
        return false;
      } else return true;
    } else if (typeOfOwner === "SINGLEOWNER") {
      if (ownersData[0]?.name && ownersData[0]?.gender && ownersData[0]?.mobilenumber) {
        setError("TL_ERROR_FILL_ALL_MANDATORY_DETAILS");
        return false;
      } else return true;
    } else if (typeOfOwner === "MULTIOWNER") {
      return ownersData.reduce((acc, ownerData) => {
        if (ownerData?.name && ownerData?.gender && ownerData?.mobilenumber) {
          setError("TL_ERROR_FILL_ALL_MANDATORY_DETAILS");
          return false;
        }
      }, true);
    }
  }

  const goNext = () => {
    if (!checkMandatoryFieldsForEachOwner(formState)) {
      let owner = formData.owners;
      let ownerStep;
      ownerStep = { ...owner, owners: formState };
      onSelect(config.key, ownerStep);
    }
  };

  const onSkip = () => onSelect();

  if (typeOfOwner === "INSTITUTIONAL") {
    return (
      <React.Fragment>
        {window.location.href.includes("/citizen") ? <Timeline currentStep={2} /> : null}
        <FormStep config={config} onSelect={goNext} onSkip={onSkip} t={t} isDisabled={false} forcedError={t(error)}>
          {formState.map((field, index) => {
            return (
              <div key={`${field}-${index}`}>
                <div>
                  <CardLabel>{`${t("TL_INSTITUTION_NAME_LABEL")}*`}</CardLabel>
                  <TextInput
                    t={t}
                    type={"text"}
                    isMandatory={true}
                    name="institutionName"
                    value={field.institutionName}
                    ValidationRequired={true}
                    onChange={(e) => handleTextInputField(index, e, "institutionName")}
                    //disable={isUpdateProperty || isEditProperty}
                    {...{
                      validation: {
                        isRequired: true,
                        pattern: "^[a-zA-Z-.`' ]*$",
                        type: "text",
                        title: t("TL_NAME_ERROR_MESSAGE"),
                      },
                    }}
                  />
                  <CardLabel>{`${t("TL_INSTITUTION_TYPE_LABEL")}*`}</CardLabel>
                  <Dropdown
                    t={t}
                    option={institutionOwnershipTypeOptions}
                    selected={field.subOwnerShipCategory}
                    select={(value) => {
                      handleDropdownInputField(index, value, "subOwnerShipCategory");
                    }}
                    optionKey="i18nKey"
                  />
                  <CardHeader>{t("TL_AUTHORIZED_PERSON_DETAILS")}</CardHeader>
                  <CardLabel>{`${t("TL_NEW_OWNER_DETAILS_NAME_LABEL")}`}</CardLabel>
                  <TextInput
                    t={t}
                    type={"text"}
                    isMandatory={false}
                    name="name"
                    value={field.name}
                    onChange={(e) => handleTextInputField(index, e, "name")}
                    ValidationRequired={true}
                    {...{
                      validation: {
                        // isRequired: true,
                        pattern: "^[a-zA-Z-.`' ]*$",
                        type: "text",
                        title: t("TL_NAME_ERROR_MESSAGE"),
                      },
                    }}
                  />
                  <CardLabel>{`${t("TL_NEW_OWNER_DESIG_LABEL")}`}</CardLabel>
                  <TextInput
                    t={t}
                    type={"text"}
                    isMandatory={false}
                    name="designation"
                    value={field.designation}
                    onChange={(e) => handleTextInputField(index, e, "designation")}
                    ValidationRequired={true}
                    //disable={isUpdateProperty || isEditProperty}
                    {...{
                      validation: {
                        // isRequired: true,
                        pattern: "^[a-zA-Z-.`' ]*$",
                        type: "text",
                        title: t("TL_NAME_ERROR_MESSAGE"),
                      },
                    }}
                  />
                  <CardLabel>{`${t("TL_MOBILE_NUMBER_LABEL")}*`}</CardLabel>
                  <div className="field-container">
                    <span className="employee-card-input employee-card-input--front" style={{ marginTop: "-1px" }}>
                      +91
                    </span>
                    <TextInput
                      type={"text"}
                      t={t}
                      isMandatory={false}
                      name="mobilenumber"
                      value={field.mobilenumber}
                      onChange={(e) => handleTextInputField(index, e, "mobilenumber")}
                      ValidationRequired={true}
                      //disable={isUpdateProperty || isEditProperty}
                      {...{
                        validation: {
                          isRequired: true,
                          pattern: "[6-9]{1}[0-9]{9}",
                          type: "tel",
                          title: t("CORE_COMMON_APPLICANT_MOBILE_NUMBER_INVALID"),
                        },
                      }}
                    />
                  </div>
                  <CardLabel>{`${t("TL_TELEPHONE_NUMBER_LABEL")}`}</CardLabel>
                  <div className="field-container">
                    <span className="employee-card-input employee-card-input--front" style={{ marginTop: "-1px" }}>
                      +91
                    </span>
                    <TextInput
                      type={"text"}
                      t={t}
                      isMandatory={false}
                      name="altContactNumber"
                      value={field.altContactNumber}
                      onChange={(e) => handleTextInputField(index, e, "altContactNumber")}
                      ValidationRequired={true}
                      //disable={isUpdateProperty || isEditProperty}
                      {...{
                        validation: {
                          // isRequired: true,
                          pattern: "[0][1-9][0-9]{9}|[1-9][0-9]{9}",
                          type: "tel",
                          title: t("CORE_COMMON_APPLICANT_MOBILE_NUMBER_INVALID"),
                        },
                      }}
                    />
                  </div>
                  <CardLabel>{`${t("NOC_APPLICANT_EMAIL_LABEL")}`}</CardLabel>
                  <TextInput
                    t={t}
                    type={"text"}
                    isMandatory={false}
                    name="emailId"
                    value={field.emailId}
                    onChange={(e) => handleTextInputField(index, e, "emailId")}
                    ValidationRequired={true}
                    //disable={isUpdateProperty || isEditProperty}
                    {...{
                      validation: {
                        // isRequired: true,
                        // pattern: getPattern("Email"),
                        type: "text",
                        title: t("TL_EMAIL_ERROR_MESSAGE"),
                      },
                    }}
                  />
                </div>
              </div>
            );
          })}
        </FormStep>
      </React.Fragment>
    );
  }

  return (
    <React.Fragment>
      {window.location.href.includes("/citizen") ? <Timeline currentStep={2} /> : null}
      <FormStep config={config} onSelect={goNext} onSkip={onSkip} t={t} isDisabled={false} forcedError={t(error)}>
        {formState.map((field, index) => {
          return (
            <div key={`${field}-${index}`}>
              <div
                style={
                  typeOfOwner === "MULTIOWNER"
                    ? {
                        border: "solid",
                        borderRadius: "5px",
                        padding: "10px",
                        paddingTop: "20px",
                        marginTop: "10px",
                        borderColor: "#f3f3f3",
                        background: "#FAFAFA",
                      }
                    : {}
                }
              >
                <CardLabel style={typeOfOwner === "MULTIOWNER" ? { marginBottom: "-15px" } : {}}>{`${t(
                  "TL_NEW_OWNER_DETAILS_NAME_LABEL"
                )}*`}</CardLabel>
                {typeOfOwner === "MULTIOWNER" && (
                  <LinkButton
                    label={
                      <div>
                        <span>
                          <svg
                            style={{ float: "right", position: "relative", bottom: "5px" }}
                            width="24"
                            height="24"
                            viewBox="0 0 24 24"
                            fill="none"
                            xmlns="http://www.w3.org/2000/svg"
                          >
                            <path
                              d="M1 16C1 17.1 1.9 18 3 18H11C12.1 18 13 17.1 13 16V4H1V16ZM14 1H10.5L9.5 0H4.5L3.5 1H0V3H14V1Z"
                              fill={!(formState.length == 1) ? "#494848" : "#FAFAFA"}
                            />
                          </svg>
                        </span>
                      </div>
                    }
                    style={{ width: "100px", display: "inline" }}
                    onClick={(e) => dispatch({ type: "REMOVE_THIS_OWNER", payload: { index } })}
                  />
                )}
                <TextInput
                  style={typeOfOwner === "MULTIOWNER" ? { background: "#FAFAFA" } : {}}
                  t={t}
                  type={"text"}
                  isMandatory={false}
                  optionKey="i18nKey"
                  name="name"
                  value={field.name}
                  onChange={(e) => handleTextInputField(index, e, "name")}
                  ValidationRequired={true}
                  //disable={isUpdateProperty || isEditProperty}
                  {...{
                    validation: {
                      isRequired: true,
                      pattern: "^[a-zA-Z-.`' ]*$",
                      type: "text",
                      title: t("TL_NAME_ERROR_MESSAGE"),
                    },
                  }}
                />
                <CardLabel>{`${t("TL_NEW_OWNER_DETAILS_GENDER_LABEL")}*`}</CardLabel>
                {!isGenderLoading ? (
                  <RadioButtons
                    t={t}
                    options={TLmenu}
                    optionsKey="i18nKey"
                    name={`gender-${index}`}
                    selectedOption={field.gender}
                    onSelect={(e) => handleRadioButtonInput(index, e, "gender")}
                    labelKey=""
                    isPTFlow={true}
                    //disabled={isUpdateProperty || isEditProperty}
                  />
                ) : (
                  <Loader />
                )}
                <CardLabel>{`${t("TL_MOBILE_NUMBER_LABEL")}*`}</CardLabel>
                <div className="field-container">
                  <span className="employee-card-input employee-card-input--front" style={{ marginTop: "-1px" }}>
                    +91
                  </span>
                  <TextInput
                    style={typeOfOwner === "MULTIOWNER" ? { background: "#FAFAFA" } : {}}
                    type={"text"}
                    t={t}
                    isMandatory={false}
                    optionKey="i18nKey"
                    name="mobilenumber"
                    value={field.mobilenumber}
                    onChange={(e) => handleTextInputField(index, e, "mobilenumber")}
                    ValidationRequired={true}
                    //disable={isUpdateProperty || isEditProperty}
                    {...{
                      validation: {
                        isRequired: true,
                        pattern: "[6-9]{1}[0-9]{9}",
                        type: "tel",
                        title: t("CORE_COMMON_APPLICANT_MOBILE_NUMBER_INVALID"),
                      },
                    }}
                  />
                </div>
                <CardLabel>{`${t("TL_NEW_OWNER_DETAILS_GUARDIAN_LABEL")}*`}</CardLabel>
                <TextInput
                  style={typeOfOwner === "MULTIOWNER" ? { background: "#FAFAFA" } : {}}
                  t={t}
                  type={"text"}
                  isMandatory={false}
                  name="fatherOrHusbandName"
                  value={field.fatherOrHusbandName}
                  onChange={(e) => handleTextInputField(index, e, "fatherOrHusbandName")}
                  ValidationRequired={true}
                  //disable={isUpdateProperty || isEditProperty}
                  {...{
                    validation: {
                      isRequired: true,
                      pattern: "^[a-zA-Z-.`' ]*$",
                      type: "text",
                      title: t("TL_NAME_ERROR_MESSAGE"),
                    },
                  }}
                />
                <CardLabel>{`${t("TL_RELATIONSHIP_WITH_GUARDIAN_LABEL")}*`}</CardLabel>
                <RadioButtons
                  t={t}
                  options={relationshipMenu}
                  optionsKey="i18nKey"
                  name={`relationship-${index}`}
                  value={field.relationship}
                  selectedOption={field.relationship}
                  onSelect={(e) => handleRadioButtonInput(index, e, "relationship")}
                  labelKey=""
                  isPTFlow={true}
                />
                {typeOfOwner === "MULTIOWNER" && (
                  <CheckBox
                    label={t("TL_PRIMARY_OWNER_LABEL")}
                    onChange={(e) => dispatch({ type: "SET_PRIMARY_OWNER", payload: { index } })}
                    value={field.isprimaryowner}
                    checked={field.isprimaryowner}
                    style={{ paddingTop: "10px" }}
                    name={`multiowner-checkbox-${index}`}
                    //disable={isUpdateProperty || isEditProperty}
                  />
                )}
              </div>
            </div>
          );
        })}
        {typeOfOwner === "MULTIOWNER" && (
          <div>
            {/* <hr color="#d6d5d4" className="break-line"></hr> */}
            <div style={{ justifyContent: "center", display: "flex", paddingBottom: "15px", color: "#FF8C00" }}>
              <button type="button" style={{ paddingTop: "10px" }} onClick={() => dispatch({ type: "ADD_NEW_OWNER" })}>
                {t("TL_ADD_OWNER_LABEL")}
              </button>
            </div>
          </div>
        )}
      </FormStep>
    </React.Fragment>
  );
};

export default SelectOwnerDetails;
import React, { useEffect, useState } from "react";
import { FormStep, TextInput, CheckBox, CardLabel, LabelFieldPair, TextArea, CitizenInfoLabel } from "@egovernments/digit-ui-react-components";
import { useLocation } from "react-router-dom";
import Timeline from "../components/TLTimeline";

const SelectOwnerAddress = ({ t, config, onSelect, userType, formData }) => {
  const [permanentAddress, setPermanentAddress] = useState(formData?.owners?.permanentAddress || "");
  const [isCorrespondenceAddress, setIsCorrespondenceAddress] = useState(formData?.owners?.isCorrespondenceAddress);
  let isedittrade = window.location.href.includes("edit-application");
  let isrenewtrade = window.location.href.includes("renew-trade");
  const { pathname: url } = useLocation();
  const editScreen = url.includes("/modify-application/");
  let ismultiple = formData?.ownershipCategory?.code.includes("SINGLEOWNER") ? false : true;

  useEffect(() => {
    if (formData?.owners?.permanentAddress == null && isrenewtrade && permanentAddress === "") {
      let obj = {
        doorNo: formData?.address?.doorNo,
        street: formData?.address?.street || formData?.address?.buildingName,
        landmark: formData?.address?.landmark,
        locality: formData?.address?.locality?.name,
        city: formData?.address?.city?.code?.split(".")[1],
        pincode: formData?.address?.pincode,
      };
      let addressDetails = "";
      for (const key in obj) {
        if (key == "pincode" || (!obj["pincode"] && key =="city")) addressDetails += obj[key] ? obj[key] : "";
        else if(obj[key]) addressDetails += obj[key] ? t(`${obj[key]}`) + ", " : "";
      }
      setPermanentAddress(addressDetails);
    }
  }, [formData]);
  function setOwnerPermanentAddress(e) {
    setPermanentAddress(e.target.value);
  }
  function setCorrespondenceAddress(e) {
    if (formData?.cpt?.details && Object.keys(formData?.cpt?.details).length > 0 && e.target.checked == true) {
      let obj = {
        doorNo: formData?.cpt?.details?.address?.doorNo,
        street: formData?.cpt?.details?.address?.street || formData?.cpt?.details?.address?.buildingName,
        landmark: formData?.cpt?.details?.address?.landmark,
        locality: formData?.cpt?.details?.address?.locality?.name,
        city: formData?.cpt?.details?.address?.city,
        pincode: formData?.address?.pincode,
      };
      let addressDetails = "";
      for (const key in obj) {
        if (key == "pincode" || (!obj["pincode"] && key =="city")) addressDetails += obj[key] ? obj[key] : "";
        else if(obj[key]) addressDetails += obj[key] ? t(`${obj[key]}`) + ", " : "";
      }
      setPermanentAddress(addressDetails);
    } else if (e.target.checked == true) {
      let obj = {
        doorNo: formData?.address?.doorNo,
        street: formData?.address?.street || formData?.address?.buildingName,
        landmark: formData?.address?.landmark,
        locality: formData?.address?.locality?.i18nkey,
        city: formData?.address?.city?.name,
        pincode: formData?.address?.pincode,
      };
      let addressDetails = "";
      for (const key in obj) {
        if (key == "pincode" || (!obj["pincode"] && key =="city")) addressDetails += obj[key] ? obj[key] : "";
        else if(obj[key]) addressDetails += obj[key] ? t(`${obj[key]}`) + ", " : "";
      }
      setPermanentAddress(addressDetails);
    } else {
      setPermanentAddress("");
    }
    setIsCorrespondenceAddress(e.target.checked);
  }

  const goNext = () => {
    if (userType === "employee") {
      onSelect(config.key, { ...formData[config.key], permanentAddress, isCorrespondenceAddress });
    } else {
      let ownerDetails = formData.owners;
      ownerDetails["permanentAddress"] = permanentAddress;
      ownerDetails["isCorrespondenceAddress"] = isCorrespondenceAddress;
      onSelect(config.key, ownerDetails);
    }
  };

  useEffect(() => {
    if (userType === "employee") {
      goNext();
    }
  }, [permanentAddress]);

  if (userType === "employee") {
    return (
      <LabelFieldPair>
        <CardLabel className="card-label-smaller" style={editScreen ? { color: "#B1B4B6" } : {}}>
          {t("PT_OWNERS_ADDRESS")}
        </CardLabel>
        <div className="field">
          <TextInput name="address" onChange={setOwnerPermanentAddress} value={permanentAddress} disable={editScreen} />
        </div>
      </LabelFieldPair>
    );
  }

  return (
    <React.Fragment>
      {window.location.href.includes("/citizen") ? <Timeline currentStep={2} /> : null}
      <FormStep config={config} t={t} onSelect={goNext} isDisabled={isedittrade || isrenewtrade ? false : !permanentAddress}>
        <TextArea
          isMandatory={false}
          optionKey="i18nKey"
          t={t}
          name="address"
          onChange={setOwnerPermanentAddress}
          value={permanentAddress}
          //disable={isUpdateProperty || isEditProperty}
        />
        {/* <CardLabel>{t("PT_OWNER_S_ADDRESS")}</CardLabel> */}
        <CheckBox
          label={t("TL_COMMON_SAME_AS_TRADE_ADDRESS")}
          onChange={setCorrespondenceAddress}
          value={isCorrespondenceAddress}
          checked={isCorrespondenceAddress || false}
          style={{ paddingTop: "10px" }}
          //disable={isUpdateProperty || isEditProperty}
        />
      </FormStep>
      {ismultiple ? <CitizenInfoLabel info={t("CS_FILE_APPLICATION_INFO_LABEL")} text={t("TL_PRIMARY_ADDR_INFO_MSG")} /> : ""}
    </React.Fragment>
  );
};

