const SubOccupancyTable = ({ edcrDetails, applicationData }) => {
  const { t } = useTranslation();
  const isMobile = window.Digit.Utils.browser.isMobile();

  const tableHeader = [
    {
      name: "BPA_TABLE_COL_FLOOR",
