import React from "react"
import { useTranslation } from "react-i18next";
import { Switch, useLocation, Route } from "react-router-dom";
import { BackButton, PrivateRoute } from "@egovernments/digit-ui-react-components";
import DocumentCategories from "./Documents/DocumentCategories";
import ViewDocument from "./Documents/ViewDocument";
import Response from './CitizenSurvey/Response';

const CitizenApp = ({ path, url, userType, tenants}) => {
    const location = useLocation();
    const { t } = useTranslation();
    const NotificationsOrWhatsNew = Digit.ComponentRegistryService.getComponent("NotificationsAndWhatsNew")
    const Events = Digit.ComponentRegistryService.getComponent("EventsListOnGround")
    const EventDetails = Digit.ComponentRegistryService.getComponent("EventDetails")
    const Documents = Digit.ComponentRegistryService.getComponent("DocumentList")
    const SurveyList = Digit.ComponentRegistryService.getComponent("SurveyList")
    const FillSurvey = Digit.ComponentRegistryService.getComponent('FillSurvey')
    const ShowSurvey = Digit.ComponentRegistryService.getComponent('ShowSurvey')
    return (
      <React.Fragment>
        <BackButton>{t("CS_COMMON_BACK")}</BackButton>
        <Switch>
          <Route
            path={`${path}/notifications`}
            component={() => <NotificationsOrWhatsNew variant="notifications" parentRoute={path} />}
          />
          <PrivateRoute
            path={`${path}/whats-new`}
            component={() => <NotificationsOrWhatsNew variant="whats-new" parentRoute={path} />}
          />
          <PrivateRoute
            exact
            path={`${path}/events`}
            component={() => <Events variant="events" parentRoute={path} />}
          />
          <PrivateRoute
            path={`${path}/events/details/:id`}
            component={() => <EventDetails parentRoute={path} />}
          />
          <PrivateRoute path={`${path}/docs`} component={() => <DocumentCategories t={t} {...{ path }} />} />
          <PrivateRoute path={`${path}/documents/viewDocument`} component={() => <ViewDocument t={t} {...{ path }} />} />
          <PrivateRoute path={`${path}/documents/list/:category/:count`} component={(props) => <Documents {...props} />} />
          <PrivateRoute path={`${path}/surveys/list`} component={(props) => <SurveyList {...props} />} />
          <PrivateRoute path={`${path}/surveys/fill-survey`} component={(props) => <FillSurvey {...props} />} />
          <PrivateRoute path={`${path}/surveys/submit-response`} component={(props) => <Response {...props} />} />
          <PrivateRoute path={`${path}/surveys/show-survey`} component={(props) => <ShowSurvey {...props} />} />
        </Switch>
      </React.Fragment>
    );
};
export default CitizenApp

import { Clock, Header, Loader, MapMarker, OnGroundEventCard } from "@egovernments/digit-ui-react-components";
import React, { useMemo } from "react";
import { useTranslation } from "react-i18next";
import { Redirect, useHistory, useLocation, useParams } from "react-router-dom";

const EventDetails = () => {
  const { t } = useTranslation();
  const location = useLocation();
  const history = useHistory();
  const { id: EventId } = useParams();

  const tenantId = Digit.ULBService.getCitizenCurrentTenant();

  const { data: EventsData, isLoading: EventsDataLoading } = Digit.Hooks.useEvents({ tenantId, variant: "events" });

  if (!Digit.UserService?.getUser()?.access_token) {
    localStorage.clear();
    sessionStorage.clear();
    return <Redirect to={{ pathname: `/digit-ui/citizen/login`, state: { from: location.pathname + location.search } }} />;
  }

  function onGroundEventCardPropsForEventDetails(DataParamsInEvent) {
    const { eventCategory, name, onGroundEventDate, onGroundEventMonth, onGroundEventTimeRange } = DataParamsInEvent;
    return { eventCategory: t(eventCategory), showEventCatergory: true, name, onGroundEventDate, onGroundEventMonth, onGroundEventTimeRange };
  }

  const FilteredEventForThisPage = useMemo(() => (!EventsDataLoading ? EventsData?.filter((i) => i.id === EventId)[0] : null), [
    EventsDataLoading,
    EventsData,
  ]);

  if (EventsDataLoading) return <Loader />;
  return (
    <div className="CitizenEngagementNotificationWrapper">
      <Header>{t("ES_TITLE_APPLICATION_DETAILS")}</Header>
      <OnGroundEventCard {...onGroundEventCardPropsForEventDetails(FilteredEventForThisPage)} />
      <div className="OnGroundEventDetailsCard">
        <p className="cardCaptionBodyS">{FilteredEventForThisPage?.description}</p>
        <div className="eventAddressAndDirection">
          <span>
            <MapMarker />
            <div>
              <p>{FilteredEventForThisPage?.eventDetails?.address}</p>
              {FilteredEventForThisPage?.eventDetails?.latitude && FilteredEventForThisPage?.eventDetails?.longitude ? (
                <a
                  className="direction"
                  target="_blank"
                  href={`https://www.google.com/maps/search/?api=1&query=${FilteredEventForThisPage?.eventDetails?.latitude}%2C${FilteredEventForThisPage?.eventDetails?.longitude}`}
                >
                  {t("CS_COMMON_GET_DIRECTIONS")}
                </a>
              ) : null}
            </div>
          </span>
        </div>
        <div className="eventTimeRange">
          <Clock />
          <p>{FilteredEventForThisPage?.onGroundEventTimeRange}</p>
        </div>
      </div>
    </div>
  );
};

