import React, { useMemo } from "react";
import { useForm, Controller } from "react-hook-form";
import { TextInput, Label, SubmitBar, LinkLabel, ActionBar, CloseSvg, Dropdown } from "@egovernments/digit-ui-react-components";
import { alphabeticalSortFunctionForTenantsBasedOnName } from "../../utils";

const Search = ({ onSearch, searchParams, searchFields, type, onClose, isInboxPage, t }) => {
    const { register, handleSubmit, formState, reset, watch, control } = useForm({
        defaultValues: searchParams,
    });
    const mobileView = innerWidth <= 640;
    const ulbs = Digit.SessionStorage.get("ENGAGEMENT_TENANTS");
    const tenantId = Digit.ULBService.getCurrentTenantId();

    const userInfo = Digit.UserService.getUser().info;
    const userUlbs = ulbs.filter(ulb => userInfo?.roles?.some(role => role?.tenantId === ulb?.code)).sort(alphabeticalSortFunctionForTenantsBasedOnName);


    const selectedTenat = useMemo(() => {
        const filtered = ulbs.filter((item) => item.code === tenantId)
        return filtered;
    }, [tenantId, ulbs])

    const getFields = (input) => {
        switch (input.type) {
            case "ulb":
                return (
                    <Controller
                    rules={{ required: true }}
                        defaultValue={selectedTenat?.[0]}
                        render={props => (
                            <Dropdown
                                option={userUlbs}
                                optionKey={"i18nKey"}
                                selected={props.value}
                                select={props.onChange}
                                t={t}
                            />
                        )}
                        name={input.name}
                        control={control}

                    />
                )
            default:
                return (
                    <Controller
                        render={(props) => <TextInput onChange={props.onChange} value={props.value} />}
                        name={input.name}
                        control={control}
                        defaultValue={null}
                    />
                )
        }
    }

    const onSubmitInput = (data) => {
        // searchFields.forEach((field) => {
        //   if (!data[field.name]) data.delete.push(field.name);
        // });

        onSearch(data);
        if (type === "mobile") {
            onClose();
        }
    }

    const clearSearch = () => {
        reset({ name: '', ulbs: { code: tenantId.code }, postedBy: "" });
        onSearch({ name: '', ulbs: { code: tenantId.code }, postedBy: "" })
    };

    const clearAll = (mobileView) => {
        const mobileViewStyles = mobileView ? { margin: 0 } : {};
        return (
            <LinkLabel style={{ display: "inline", ...mobileViewStyles }} onClick={clearSearch}>
                {t("ES_COMMON_CLEAR_SEARCH")}
            </LinkLabel>
        );
    };

    return (
        <form onSubmit={handleSubmit(onSubmitInput)}>
            <div className="search-container" style={{ width: "auto", marginLeft: isInboxPage ? "24px" : "revert" }}>
                <div className="search-complaint-container">
                    {(type === "mobile" || mobileView) && (
                        <div className="complaint-header">
                            <h2>{t("ES_COMMON_SEARCH_BY")}</h2>
                            <span onClick={onClose}>
                                <CloseSvg />
                            </span>
                        </div>
                    )}
                    <div className={"complaint-input-container for-pt " + (!isInboxPage ? "for-search" : "")} style={{ width: "100%" }}>
                        {searchFields
                            ?.map((input, index) => (
                                <div key={input.name} className="input-fields">
                                    {/* <span className={index === 0 ? "complaint-input" : "mobile-input"}> */}
                                    <span className={"mobile-input"}>
                                        <Label>{t(input.label) + ` ${input.isMendatory ? "*" : ""}`}</Label>
                                        {getFields(input)}
                                    </span>
                                    {formState?.dirtyFields?.[input.name] ? (
                                        <span
                                            style={{ fontWeight: "700", color: "rgba(212, 53, 28)", paddingLeft: "8px", marginTop: "-20px", fontSize: "12px" }}
                                            className="inbox-search-form-error"
                                        >
                                            {formState?.errors?.[input.name]?.message}
                                        </span>
                                    ) : null}
                                </div>
                            ))}

                        {/* {isInboxPage && ( */}
                        {/* // <div style={{ gridColumn: "2/3", textAlign: "right", paddingTop: "10px" }} className="input-fields"> */}
                        {/* <div>{clearAll()}</div> */}
                        {/* // </div> */}
                        {/* )} */}

                        {type === "desktop" && !mobileView && (
                            <div style={{ maxWidth: "unset", marginLeft: "unset" }} className="search-submit-wrapper">
                                <SubmitBar
                                    className="submit-bar-search"
                                    label={t("ES_COMMON_SEARCH")}
                                    // disabled={!!Object.keys(formState.errors).length || formValueEmpty()}
                                    submit
                                />
                            </div>
                        )}
                        {type === "desktop" && !mobileView && (
                            <div className="document-clear-all">{clearAll()}</div>)
                        }
                    </div>
                </div>
            </div>
            {(type === "mobile" || mobileView) && (
                <ActionBar className="clear-search-container">
                    <button className="clear-search" style={{ flex: 1 }}>
                        {clearAll(mobileView)}
                    </button>
                    <SubmitBar disabled={!!Object.keys(formState.errors).length} label={t("ES_COMMON_SEARCH")} style={{ flex: 1 }} submit={true} />
                </ActionBar>
            )}
        </form>
    )

}

export default Search;

import React, { useState } from "react";
import { CloseSvg, Loader, SubmitBar, Dropdown, RefreshIcon } from "@egovernments/digit-ui-react-components";
import { useTranslation } from "react-i18next";


const Filter = ({ type = "desktop", onClose, onSearch, onFilterChange, searchParams }) => {
    const { t } = useTranslation();
    const [localSearchParams, setLocalSearchParams] = useState(() => ({ ...searchParams }));
    const tenantId = Digit.ULBService.getCurrentTenantId();
    const stateId = tenantId?.split('.')[0];
    const currrentUlb = Digit.ULBService.getCurrentUlb();
    const { data: categoryData, isLoading } = Digit.Hooks.engagement.useMDMS(stateId, "DocumentUploader", ["UlbLevelCategories"], {
        select: (d) => {
            const data = d?.DocumentUploader?.UlbLevelCategories?.filter?.((e) => e.ulb === currrentUlb.code);
            return data[0].categoryList.map((category) => ({ category }));
        },
    });

    const clearAll = () => {
        setLocalSearchParams({ category: null, name: null, postedBy: null, })
        onFilterChange({ category: null, name: null, postedBy: null, });
        onClose?.();
    };

    const applyLocalFilters = () => {
        onFilterChange(localSearchParams);
        onClose?.();
    };
    const handleChange = (data) => {
        setLocalSearchParams({ ...localSearchParams, ...data });
    };


    if (isLoading) {
        return (
            <Loader />
        );
    }
    return (
        <div className="filter">
            <div className="filter-card">
                <div className="heading">
                    <div className="filter-label">{t("ES_COMMON_FILTER_BY")}:</div>
                    <div className="clearAll" onClick={clearAll}>
                        {t("ES_COMMON_CLEAR_ALL")}
                    </div>
                    {type === "desktop" && (
                        <span className="clear-search" onClick={clearAll}>
                            <RefreshIcon />
                        </span>
                    )}
                    {type === "mobile" && (
                        <span onClick={onClose}>
                            <CloseSvg />
                        </span>
                    )}
                </div>
                <div className="filter-label">{`${t('DOCUMENTS_CATEGORY_CARD_LABEL')}`}</div>
                <Dropdown
                    option={categoryData}
                    optionKey={"category"}
                    selected={localSearchParams}
                    select={value => {
                        handleChange(value)
                    }
                    }
                    t={t}
                />
                <div>
                    <SubmitBar style={{ width: '100%' }} onSubmit={() => applyLocalFilters()} label={t("ES_COMMON_APPLY")} />
                </div>
            </div>
        </div>
    )
}

export default Filter;

import React from "react";
import { Card, Loader } from "@egovernments/digit-ui-react-components";
import { Link } from "react-router-dom";

import EventLink from "../Events/EventLink";
import DocumentNotificationTable from "./DocumentNotificationTable";
import Search from "./Search";
import Filter from "./Filter";
import { openDocumentLink, openUploadedDocument } from "../../utils";



const getDocumentDetailsPath = (document) => {
  return {
    pathname: `inbox/details/${document.name}`,
    state: { details: document }
  }

}

const GetCell = (value) => <span className="cell-text styled-cell">{value}</span>;
const getDocumentDetails = (value = "", link, t) => <span className="document-table-docs-columns"><Link className="link" to={link} >{value.length ? value : t('CE_DOCUMENT_TITLE')}</Link></span>
const getDocumentCell = (name = "mSeva", link, docLink, t) => (
  <span className="document-table-docs-columns" >
    {link?.length ? <span className="link" onClick={() => openUploadedDocument(link, name)} > {t('CE_DOCUMENT_VIEW_LINK')} </span> : null}
    {docLink.length ? <span className="link" onClick={() => openDocumentLink(docLink, name)}> {t('CE_DOCUMENT_OPEN_LINK')} </span> : null}
  </span>);


const DocumentDesktopInbox = ({ isLoading, data, t, onSearch, title, iconName, links, onSort, sortParams, globalSearch, searchFields, searchParams, onFilterChange, pageSizeLimit, totalRecords, currentPage, onNextPage, onPrevPage, onPageSizeChange }) => {
  const columns = React.useMemo(() => [
    {
      Header: t('CE_TABLE_DOCUMENT_NAME'),
      accessor: (row) => getDocumentDetails(row?.name, getDocumentDetailsPath(row), t),
    },
    {
      Header: t('DOCUMENTS_CATEGORY_CARD_LABEL'),
      accessor: (row) => GetCell(row?.category ? t(`${row?.category}`) : "")
    },
    {
      Header: t('CE_TABLE_DOCUMENT_LINK'),
      accessor: (row) => getDocumentCell(row?.name, row.filestoreId, row.documentLink || "", t)
    },
    {
      Header: t('CE_TABLE_DOCUMENT_POSTED_BY'),
      accessor: (row) => GetCell(row.postedBy)
    },

  ], [])

  let result;
  if (isLoading) {
    result = <Loader />;
  } else if (!data || data?.length === 0) {
    result = (
      <Card style={{ marginTop: 20, display: 'flex', flexDirection: 'column', alignItems: 'center', justifyContent: 'center' }}>
        {t("CE_DOCUMENTS_NOT_FOUND")}<br />
        <Link className="link" to={`/digit-ui/employee/engagement/documents/inbox/new-doc`}>{t('NEW_DOCUMENT_TEXT')}</Link>
      </Card>
    );
  } else if (data?.length > 0) {
    result = (
      <DocumentNotificationTable
        t={t}
        data={data}
        columns={columns}
        onSort={onSort}
        sortParams={sortParams}
        globalSearch={globalSearch}
        onSearch={searchParams}
        pageSizeLimit={pageSizeLimit}
        totalRecords={totalRecords}
        currentPage={currentPage}
        onNextPage={onNextPage}
        onPrevPage={onPrevPage}
        onPageSizeChange={onPageSizeChange}
        getCellProps={(cellInfo) => {
          return {
            style: {
              minWidth: cellInfo.column.Header === t("CE_TABLE_DOCUMENT_NAME") ? "240px" : "",
              padding: "20px 18px",
              fontSize: "16px",
            },
          };
        }}
      />
    );
  }

  return (
    <div className="inbox-container">
      <div className="filters-container">
        <EventLink title={title} icon={iconName} links={links} />
        <div>
          <Filter onFilterChange={onFilterChange} searchParams={searchParams} />
        </div>
      </div>
      <div style={{ flex: 1 }}>
        <Search
          t={t}
          onSearch={onSearch}
          type="desktop"
          searchFields={searchFields}
          isInboxPage={true}
          searchParams={searchParams}
        />
        <div className="result" style={{ marginLeft: "24px", flex: 1 }}>
          {result}
        </div>
      </div>
    </div>
  )
}

export default DocumentDesktopInbox;

import React, { useState, useEffect } from "react";
import { Card, DetailsCard, Loader, PopUp, SearchAction, FilterAction } from "@egovernments/digit-ui-react-components";
import Filter from "./Filter";
import Search from "./Search";
import { useHistory, Link } from "react-router-dom";
import { areEqual } from "../../utils";

const ApplicationCard = ({
  searchFields,
  searchParams,
  onFilterChange,
  onSearch,
  t,
  data,
  responseData,
}) => {
  const [type, setType] = useState("");
  const [popup, setPopup] = useState(false);
  const [params, setParams] = useState(searchParams);
  const tenantId = Digit.ULBService.getCurrentTenantId();
  const history = useHistory()
  useEffect(() => {
    if (type) setPopup(true);
  }, [type]);

  const selectParams = (param) => {
    setParams((o) => ({ ...o, ...param }));
  };

  const handlePopupClose = () => {
    setPopup(false);
    setParams(searchParams);
  };

  const redirectToDetailsPage = (data) => {
    const details = responseData?.find((item) => (areEqual(item.postedBy, data["Posted By"]) && areEqual(item.name, data["Document Name"])));
    if (details) {
      history.push(`/digit-ui/employee/engagement/documents/inbox/details/${details?.name}`, { details })
    }
  }

  let result;
  if (data?.length === 0) {
    result = (
      <Card style={{ marginTop: 20, display: 'flex', flexDirection: 'column', alignItems: 'center', justifyContent: 'center' }}>
        {t("CE_DOCUMENTS_NOT_FOUND")}<br />
        <Link className="link" to={`/digit-ui/employee/engagement/documents/inbox/new-doc`}>{t('NEW_DOCUMENT_TEXT')}</Link>
      </Card>
    );
  }
  else if (data && data?.length > 0) {
    result = <DetailsCard data={data} handleSelect={() => { }} handleDetailCardClick={redirectToDetailsPage} />
  }
  return (
    <React.Fragment>
      <div className="searchBox">
        {onSearch && (
          <SearchAction
            text="SEARCH"
            handleActionClick={() => {
              setType("SEARCH");
              setPopup(true);
            }}
          />
        )}
        <FilterAction
          text="FILTER"
          handleActionClick={() => {
            setType("FILTER");
            setPopup(true);
          }}
        />
      </div>
      {result}
      {popup && (
        <PopUp>
          {type === "FILTER" && (
            <div className="popup-module">
              {
                <Filter
                  onFilterChange={onFilterChange}
                  onClose={handlePopupClose}
                  onSearch={onSearch}
                  type="mobile"
                  searchParams={params}
                />
              }
            </div>
          )}
          {type === "SEARCH" && (
            <div className="popup-module">
              <Search
                t={t}
                type="mobile"
                onClose={handlePopupClose}
                onSearch={onSearch}
                searchParams={searchParams}
                searchFields={searchFields}
              />
            </div>
          )}
        </PopUp>
      )}
    </React.Fragment>
  )
};

