        });
      } else {
        document.push({
          documentType: ob.documentType,
          fileStoreId: ob.fileStoreId,
          fileStore: ob.fileStoreId,
          fileName: "",
          fileUrl: "",
          additionalDetails: {},
        });
      }
    });
  document = [...document, ...(PrevStateDocs ? PrevStateDocs : [])];
