      skip={onSkip}
      t={t}
      position={geoLocation}
      //onSave={() => onSelect("address", { geoLocation, pincode })}
      //onSave={() => selectPincode(pincode)}
      onSave={() => onSave(geoLocation,pincode,placeName)}
      onChange={(code, location, place) => onChange(code, location, place)}
